import React from 'react';
import { useForm } from "react-hook-form";
const handleImageUpload = event =>{
    console.log(event.target.files[0])
    const imageData = new FormData();
    imageData.set('key','91b4840911bc2eccb83a38fe07749f06');
    imageData.append('image', event.target.files[0]);
}

const AddProduct = () => {

    const { register, handleSubmit, watch, errors } = useForm();
    const onSubmit = data => console.log(data);
    
    return (
        <div style={{border: '1px solid black', borderRadius:'10px', padding:'10px'}} >
        
        <h1> Add your Products here </h1>
        <form onSubmit={handleSubmit(onSubmit)}>
        <input name="Name" defaultValue="" placeholder="Enter Name"  /> 
        {errors.ProductNameRequired && <span>Product Name is required</span>}   
        <p> Product Name </p>
        
        <input name="Price" placeholder="Enter Price" ref={({ required: true })} />
        {errors.ProductPriceRequired && <span>Price is required</span>}
        <p> Add Price </p>   
        
       <input name="Quantity" placeholder="Enter Quantity" ref={({ required: true })} />
        {errors.QuantityRequired && <span>Quantity is required</span>}
        <p> Quantity </p>
       
       <input name="Image" type="file" onChange={handleImageUpload} ref={({ required: true })} />
        {errors.ImageRequired && <span>Product Image is required</span>}
       
       <br/>
       <br/>
      <input  class="btn btn-success" type="submit" />
    </form>
    </div>
    );
};

export default AddProduct;
