import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faGripHorizontal, faPlus, faUserEdit } from '@fortawesome/free-solid-svg-icons';

const ManageProducts = () => {
    const [product, setProducts] = useState([])
    
    useEffect(() => {
        fetch('http://localhost:5055/products')
        .then(res => res.json())
        .then(data => setProducts(data))
    })

    return (
        <div >
             <div className="card bg-dark col-md-4 " >
        <div className="card-header ">Admin Features</div>
        <div className="list-group list-group-flush">
         <Link to="/manageProducts" > <button className="btn btn-outline-primary" style={{margin: '5%'}} > <FontAwesomeIcon icon={faGripHorizontal} ></FontAwesomeIcon>  Manage Products</button></Link>
         <Link to="/admin" > <button className="btn btn-outline-success" style={{margin: '5%'}} > <FontAwesomeIcon icon={faPlus} ></FontAwesomeIcon>  Add Products</button></Link>
         <Link to="/manageProducts" > <button className="btn btn-outline-success" style={{margin: '5%'}} > <FontAwesomeIcon icon={faUserEdit} ></FontAwesomeIcon>  Edit Products</button></Link>
        </div>
      </div>
        {
            product.map(product => <p  product={product} > {product.name} <button onClick='deleteProduct(${product._id})' className="btn btn-outline-danger"> Delete Product </button> </p>  )
        }
        </div>
    );
};

export default ManageProducts;
