import React, { useState } from 'react';
import firebase from "firebase/app";
import "firebase/auth";
import firebaseConfig from './firebase.config';

firebase.initializeApp(firebaseConfig);

const Login = () => {

    const [user, setUser] = useState({
        isSignedIn: false,
        name: '',
        email: '',
        photo: '',
    })

    const provider = new firebase.auth.GoogleAuthProvider();


    const handleSignIn = () => {
        firebase.auth().signInWithPopup(provider)
        .then(res => {
            const {displayName, photoURL, email} = res.user;
            
            console.log(displayName, photoURL, email);
        })
        .catch(err =>{
            console.log(err);
            console.log(err.message);
        })
        console.log('Sign in clicked')
    }

    return (
        <div>
            <h1> if you login, You logged in! </h1>
            <div className="text-center m-4" style={{ boxShadow: "10px 10px 20px lightgray", padding: "30px", color: "gray", backgroundColor: "rgb(240, 236, 241)" }}>
      <h3 >Please sign in with your google account to continue!</h3> <br />
      <button className="btn btn-light mx-auto w-50 m-4 p-2" onClick={handleSignIn} ><img style={{ width: "30px" }} src="https://raw.githubusercontent.com/ProgrammingHero1/travel-guru/master/Icon/google.png" alt="" /> Google Sign in</button>
    </div>
        </div>
    );
};

export default Login;
