import React, { useContext, useEffect, useState } from 'react';
import { UserContext } from '../../App';


const Orders = () => {
    const [loggedInUser, setLoggedInUser] = useContext(UserContext);
    const [orders, setOrders] = useState([]);


    useEffect(() =>{
        fetch('http://localhost:5055/orders?email=' +loggedInUser.email,{
            method: 'GET',
            headers: { 
                authorization: `Bearer ${sessionStorage.getItem('token')}`,
                'Content-Type': 'application/json'},

        })
        .then(res => res.json())
        .then(data => setOrders(data));
    },[])

    return (
        // <div>
        //     <h1> Your Recent Orders are here.. </h1>
        //     <h3> {loggedInUser.name} {orders.email} You have: {orders.length} orders </h3>
            // {
            //      orders.map(order => <div> {order.email}  {order.name} from: {(new Date(order.pickedDate).toDateString('dd/MM/yyyy'))} </div> )
            //  }
       <div class="card text-white bg-primary mb-5" >
  <div class="card-header"> <h3> {loggedInUser.name} you have {orders.length} Order/s </h3> </div>
  <div class="card-body card-title ">
            {
                 orders.map(order => <h3>Product Name: <br/> {order.name} - {order.weight}
                 <br/> <br/> Price: ${order.price} <br/><br/> <small>Picking Up date: <br/>  
                   {(new Date(order.pickedDate).toDateString('dd/MM/yyyy'))} </small> </h3> )
             }
    <p class="card-text">We Deliver the products by your given dates. But </p>
  </div>
</div>
    );
};

export default Orders;
