import React from 'react';
import { useEffect } from 'react';
import { useState } from 'react';
import Products from '../Products/Products';


const Home = () => {
    const [product, setProducts] = useState([])
    const [loading, setLoading] = useState(true);

    useEffect(() =>{
       fetch('http://localhost:5055/products')
       .then(res => res.json()) 
       .then(data => {
           setProducts(data)
           setLoading(false); 
        })
       
    }, [])

    return (
       
        <div className="row" >    
            {
             loading ? <div class="spinner-border" style={{ text, width: '3rem', height: '3rem'}} role="status">
             <span class="visually-hidden"></span>
           </div> : product.map(product =><Products product={product}> </Products>)
            }
        </div>    

    );
};

export default Home;
