import React from 'react';
import { useForm } from "react-hook-form";

const AddProduct = () => {

    const { register, handleSubmit, watch, errors } = useForm();
    const onSubmit = data => console.log(data);
    
    return (
        <div style={{border: '3px solid black', borderRadius:'10px', padding:'10px'}} >
        <h1> Add your Products here </h1>
        
        <form onSubmit={handleSubmit(onSubmit)}>
        <h5> Product Name </h5>
        <input name="example" defaultValue="" placeholder="Enter Name" ref={register} />
        
        <p> Add Price </p>
        <input name="exampleRequired" placeholder="Add Price" ref={register({ required: true })} />
        {errors.exampleRequired && <span>This field is required</span>}
       <br/>  
       <br/>
      <input  class="btn btn-success" type="submit" />
    </form>
    </div>
    );
};

export default AddProduct;
