import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router';

const CheckOut = () => {
    const {id} = useParams();

    const [products, setProducts] = useState({});
    useEffect(()=>{
        const url = `http://localhost:5055/products/${id}`;
        fetch(url)
        .then(res => res.json())
        .then(data =>setProducts(data));
    },[id])
    return (
        <div>
            <h1> Checkout </h1>
            <br/>
            <div class="card text">
            <div class="card-header">
             <h5> Description <span style={{marginLeft:'410px'}} className='text-muted' > Quantity </span> <span style={{marginLeft:'345px'}} className='text-muted' > Price </span> </h5>  
            </div>
        <div class="card-body">
        <h4 class="card-title">{products.name} - {products.weight} <span style={{marginLeft:'352px'}} > 1 </span> <span style={{marginLeft:'380px'}} > ${products.price} </span> </h4>
        <p class="card-text"></p>
    
        </div>
    <div class="card-footer text-muted">
   <h5> Total <span style={{marginLeft:'900px'}} > ${products.price} </span> </h5> 
   <br/> 
        <button className="btn btn-outline-dark" style={{marginLeft:'91%'}} type="submit"> Checkout </button>
    </div>
    </div>
        


        </div>
    );
};

export default CheckOut;
