import axios from 'axios';
import React from 'react';
import { useForm } from "react-hook-form";

const handleImageUpload = event =>{
    console.log(event.target.files[0])
    const imageData = new FormData();
    imageData.set('key','91b4840911bc2eccb83a38fe07749f06');
    imageData.append('image', event.target.files[0]);

    axios.post('https://api.imgbb.com/1/upload',imageData)
    .then(function(response) {
        console.log(response.data.data.display_url);
    })
    .catch(function(error){
        console.log(error);
    });
}

const AddProduct = () => {

    const { register, handleSubmit, watch, errors } = useForm();
    
    
    const onSubmit = data => {
        
        console.log(data)
    
    };
    


    return (
        <div style={{border: '1px solid black', borderRadius:'10px', padding:'10px'}} >
        
        <h1> Add your Products here </h1>
        <form onSubmit={handleSubmit(onSubmit)}>
        <input name="Name" defaultValue="New Exciting Product" placeholder="Enter Name"  /> 
         
        <p> Product Name </p>
        
        <input name="Price" placeholder="Enter Price" ref={({ required: true })} />
        
        <p> Add Price </p>   
        
       <input name="Quantity" placeholder="Enter Quantity"  />
        
        <p> Quantity </p>
       
       <input name="Image" type="file" onChange={handleImageUpload}  />
      
       
       <br/>
       <br/>
      <input Submittype="submit" />
    </form>
    </div>
    );
};

export default AddProduct;
