import React, { useContext, useEffect, useState } from "react";
import { useParams } from "react-router";
import Grid from "@material-ui/core/Grid";
import DateFnsUtils from "@date-io/date-fns";
import {
  MuiPickersUtilsProvider,
  KeyboardDatePicker,
} from "@material-ui/pickers";
import { UserContext } from "../../App";

const CheckOut = () => {
  const { id } = useParams();

  const [loggedInUser, setLoggedInUser] = useContext(UserContext);

  
  const [products, setProducts] = useState({});
  useEffect(() => {
    const url = `http://localhost:5055/products/${id}`;
    fetch(url)
      .then((res) => res.json())
      .then((data) => setProducts(data));
  }, [id]);

  const [selectedDate, setSelectedDate] = useState({
    pickedDate: new Date()
  }
  );

  const handleDateChange = (date) => {
    const newDates = {...selectedDate}
    newDates.pickedDate = date;
    setSelectedDate(newDates);
  };

  const handleChecking = () => {
    const newChecking = {...loggedInUser,...selectedDate};
    fetch('http://localhost:5055/addOrder',{
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(newChecking)
    })
    .then((res) => res.json())
    .then(data => console.log(data))
  }

  return (
    
    <div>
      
      <h1> Checkout Products </h1>
      
      <br />
      
      <div class="card mb-4" style={{ maxwidth: "540px" }}>
        
        <div class="row g-0">
          
          <div class="col-md-4">
            <img
              style={{ width: "400px", borderRadius: "5px" }}
              src={products.imageURL}
              alt="..."
            ></img>
          </div>
          
          <div class="col-md-8">
            <div className="card-header ">
              <h3> Product Description </h3>
            </div>
            
            <div class="card-body">
              
              <h4 class="card-title">{products.name}</h4>
              
              <h5> {products.weight} </h5>
              
              <p class="card-text">
                <small class="text-muted">Quantity: 1</small>
              </p>
              
              <p class="card-text">
                Lorem, ipsum dolor sit amet consectetur adipisicing elit. Saepe
                officia laborum exercitationem doloribus sunt velit in inventore
                ab corrupti ullam laudantium facere odit vitae est excepturi
                beatae, aspernatur explicabo unde
              </p>
              
              <h3> Price: ${products.price} </h3>
              
              <MuiPickersUtilsProvider utils={DateFnsUtils}>
                <Grid container justify="">
                  <KeyboardDatePicker
                    disableToolbar
                    variant="inline"
                    format="dd/MM/yyyy"
                    margin="normal"
                    id="date-picker-inline"
                    label="Please ensure a pick up date"
                    value={selectedDate.pickedDate}
                    onChange={handleDateChange}
                    KeyboardButtonProps={{
                      "aria-label": "change date",
                    }}
                  />
                </Grid>
              </MuiPickersUtilsProvider>

              
                <button
                  className="btn btn-outline-success"
                  style={{ marginLeft: "58%" }}
                  type="submit" onClick={handleChecking}
                >
                  {" "}
                  Proceed to Checkout{" "}
                </button>
              
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CheckOut;
